void dfs(int r, int c) {
  m[r][c] = 2;
  if (r && m[r-1][c]==1) dfs(r-1, c);
  if (r<11 && m[r+1][c]==1) dfs(r+1, c);
  if (c && m[r][c-1]==1) dfs(r, c-1);
  if (c<11 && m[r][c+1]==1) dfs(r, c+1);
}

