bool is1(char c){
    if(c=='0'){
        return 0;
    }else{
        return 1;
    }
}
int main(){
    char tmp;
    while(cin >> tmp){
        vector<vector<bool>> is(12, vector<bool>(12));
        for(int i=0; i<12; i++){
            for(int j=0; j<12; j++){
                if(!(i==0 && j==0)) cin >> tmp;
                is[i][j]=is1(tmp);
            }
        }

        int cnt=0;
        for(int i=0; i<12; i++){
            for(int j=0; j<12; j++){
                if(is[i][j]){
                    cnt++;
                    stack<pair<int, int>> s;
                    s.push({i,j});
                    while(!s.empty()){
                        int x=s.top().first;
                        int y=s.top().second;
                        s.pop();

                        is[x][y]=0;

                        if(x>0 && is[x-1][y]) s.push({x-1,y});
                        if(x<11 && is[x+1][y]) s.push({x+1,y});
                        if(y>0 && is[x][y-1]) s.push({x,y-1});
                        if(y<11 && is[x][y+1]) s.push({x,y+1});
                    }
                }
            }
        }
        cout << cnt << endl;

    }
}

