char field[16][16];

void dfs(int x, int y)
{
    field[x][y] = '0';
    if(field[x-1][y]=='1' && (x-1)>=0)
        dfs(x-1, y);
    if(field[x+1][y]=='1')
        dfs(x+1, y);
    if(field[x][y-1]=='1' && (y-1)>=0)
