void aruku(char** house,int w,int h,int row,int col){
	if(house[col][row]=='0')return;
	house[col][row]='0';
	if(row+1<w)aruku(house,w,h,row+1,col);
	if(col+1<h)aruku(house,w,h,row,col+1);
	if(row-1>=0)aruku(house,w,h,row-1,col);
	if(col-1>=0)aruku(house,w,h,row,col-1);
return;
}

int tansaku( char** house,int w,int h){
	int row=0;int col=0;int cnt=0;
	for(int i=0;i<h;i++)
	for(int j=0;j<w;j++){
		if(house[i][j]=='1'){row=j;col=i;cnt++;
			aruku(house,w,h,row,col);
		}
	}
	return cnt;
}


