void flip( int x, int y ){
    field[P(x,y)] = false;
    if( x>0 && field[P(x-1,y)] )
        flip( x-1, y );
    if( x<11 && field[P(x+1,y)] )
        flip( x+1, y );
    if( y>0 && field[P(x,y-1)] )
        flip( x, y-1 );
    if( y<11 && field[P(x,y+1)] )
        flip( x, y+1 );
}

