	map[i] = 0;
	if (i / 12 > 0) {
		if (map[i - 12] == 1) {
			l.push_front(i - 12);
		}
	}
	if (i / 12 < 11) {
		if (map[i + 12] == 1) {
			l.push_front(i + 12);
		}
	}
	if (i % 12 > 0) {
		if (map[i - 1] == 1) {
			l.push_front(i - 1);
		}
	}
	if (i % 12 < 11) {
		if (map[i + 1] == 1) {
			l.push_front(i + 1);
		}
	}
	l.unique();
	if (l.empty()) {
		return;
	}
	int front = l.front();
	l.pop_front();
	loop(front);
}

