void init(){
	for(int i=0; i<14; i++){
		for(int j=0; j<14; j++){
			f[i][j]=false;
		}
	}
	ans=0;
}

void calc(int x,int y,bool plus){
	if(x>=14||y>=14)return;
	if(f[x][y]||s[x][y]=='0')return;
	if(!plus)ans++;
	f[x][y]=true;
	calc(x-1,y,1);
	calc(x,y-1,1);
	calc(x+1,y,1);
	calc(x,y+1,1);
}

