}

#include<iostream>
#include<stack>
using namespace std;

class P
{
	public:
	int x,y;
	P(int X,int Y)
	{
		x=X;
		y=Y;
	}
};

int main()
{
	int ans=0;
	char field[52][52]={""};
	int flag[52][52]={0};
	int i,j;
	stack<P> st;
	int set[2]={1,-1};

	for(int i=1;i<=12;i++)
	{
		for(int j=1;j<=12;j++)
		{
			cin>>field[i][j];		
		}
	}

	for(int i=1;i<=12;i++)
	{
		for(int j=1;j<=12;j++)
		{
			if(field[i][j]!='1' || flag[i][j]==1)continue;
			st.push(P(i,j));
			flag[i][j]=1;
			ans++;

			while(!st.empty())
			{
				int x2=st.top().x;
				int y2=st.top().y;
				st.pop();

				for(int k=0;k<2;k++)
				{
					if(field[x2+set[k]][y2]!='1' ||flag[x2+set[k]][y2]==1)continue;
					flag[x2+set[k]][y2]=1;
					st.push(P(x2+set[k],y2));
				}
				for(int k=0;k<2;k++)
				{
					if(field[x2][y2+set[k]]!='1' ||flag[x2][y2+set[k]])continue;
					flag[x2][y2+set[k]]=1;
					st.push(P(x2,y2+set[k]));
				}
			}

		}
	}
	cout<<ans<<endl;
	return 0;
}

