void search(std::vector<std::string> &grid, int r, int c) {
    if (!(0 <= r && r < 12 && 0 <= c && c < 12))
        return;

    if (grid[r][c] == '0') {
        return;
    }
    grid[r][c] = '0';
    for (int i = 0; i<4; i++) {
        int newr = r + dx[i], newc = c + dy[i];
        if (!(0 <= newr && newr < 12 && 0 <= newc && newc < 12))
            continue;

        if (grid[newr][newc] == '0') {
            continue;
        }

        search(grid, newr, newc);
    }
