    bool merge(int x, int y){
        x = find(x); y = find(y);
        if (x != y) {
            if (par[y] < par[x]) swap(x, y);
            par[x] += par[y]; par[y] = x;
            cnt--;
        }
        return x != y;
    }
    bool get(int x, int y){
        return find(x) == find(y);
    }
    int find(int x){
        return par[x] < 0 ? x : par[x] = find(par[x]);
    }
};

