        par[i] = i; // i 番目の頂点のグループは最初は i
    }
}

int find(int x)
{
    if (par[x] == x){
        return (x);
    }
    return (par[x] = find(par[x]));
}

void merge(int x, int y)
{
    x = find(x);
    y = find(y);

    if (x != y){
        par[y] = x;
    }
}

int same(int x, int y)
{
    return (find(x) == find(y));
}

