	int field[COL][ROW];

void mapping(int y, int x){
	//cout<<"mapping("<<y<<", "<<x<<")\n";
	if(field[y][x]==1){
		field[y][x]=0;
		if(y+1<COL) mapping(y+1,x);
		if(x+1<COL) mapping(y,x+1);
		if(y-1>=0)  mapping(y-1,x);
		if(x-1>=0)  mapping(y,x-1);
	}
	return;
}

