			if(stage[y][x]==1){
				count++;
				queue<P> que;
				que.push(P(x,y));

				while(!que.empty()){
					int x = que.front().first;
					int y = que.front().second;

					que.pop();

					stage[y][x] = 0;
					for(int i=-1;i<=1;i++){
						for(int j=-1;j<=1;j++){
							if(i*j != 0 || i==j && i==0) continue;
							int dx = x + i;
							int dy = y + j;

							if(dx >= 0 && dx < 12 && dy >= 0 && dy < 12){

								if(stage[dy][dx] == 1){
									que.push(P(dx,dy));
								}
							}

						}
					}
				}
			}
		}
	}
	return count;
}

