		if (z[a][b + 1] == 1 && x[a][b + 1] == 0) {
			Q.push(Q.front() + 1);
			x[a][b + 1] = col;
		}
		if (z[a][b - 1] == 1 && x[a][b - 1] == 0) {
			Q.push(Q.front() - 1);
			x[a][b - 1] = col;
