			for (int l = 1; l <= 12; l++)
			{
				if (m[i][l])
				{
					ans++;
					m[i][l] = false;
					queue<pii> que;
					que.push(pii(i, l));
					while (!que.empty())
					{
						pii now = que.front(); que.pop();
						for (int k = 0; k < 4; k++)
						{
							int x = now.first + dx[k];
							int y = now.second + dy[k];
							if (m[x][y])
							{
