}uf;

void bfs(int sy, int sx) {
	queue<int> que;
	que.push(sy*w + sx);

	while( !que.empty() ) {
		int now = que.front();
		que.pop();

		if ( data[now/w][now%w] == '0' )
			continue;
		if ( uf.root(now) != now ) 	//すでに調べている(マージされている)ならcontinue
			continue;

		uf.marge(sy*w + sx, now);	//nowをsy*w + sxにくっつける(の逆をしてみた。無限ループするかも)

		for(int i = 0; i < 4; i++ ) {
			int ny = now/w + dy[i];
			int nx = now%w + dx[i];
			if ( 0 <= ny && ny < h && 0 <= nx && nx < w )
				que.push(ny * w + nx);
		}
	}
}

int main() {
	int i, j;
	while( cin >> data[0] ) {
		for( i = 1; i < h; i++ )
			cin >> data[i];

		uf.ini(h*w);

		int minus = 0;
		for( i = 0; i < h; i++ ) {
			for( j = 0; j < w; j++ ) {
				minus += (data[i][j] == '0');
				bfs(i, j);
			}
		}
		cout << uf.size - minus << endl;
	}
	return 0;
}

