	map[i] = 0;
	if ((i % 144) / 12 > 0) {
		if (map[i - 12] == 1) {
			loop(i - 12);
		}
	}
	if ((i % 144) / 12 < 11) {
		if (map[i + 12] == 1) {
			loop(i + 12);
		}
	}
	if ((i % 144) % 12 > 0) {
		if (map[i - 1] == 1) {
			loop(i - 1);
		}
	}
	if ((i % 144) % 12 < 11) {
		if (map[i + 1] == 1) {
			loop(i + 1);
		}
	}
}

