	for (int i = 0; i < 12 * 12 * 20; i++) {
		if (map[i] == 1) {
			l.push_front(i);
			loop(i);
			count++;
		}
		if (i % 144 == 143) {
			cout << count << endl;
			count = 0;
		}
	}
	return 0;
}

