bool mark[12][12];
int dx[] = {1, -1, 0, 0};
int dy[] = {0, 0, 1, -1};

void dfs(int y, int x, vector<string> &tb){
	mark[y][x] = true;

	FOR(i, 4){
		if (x + dx[i] < 0 || x + dx[i] >= 12 || y + dy[i] < 0 || y + dy[i] >= 12) continue;
		if (tb[y + dy[i]][x + dx[i]] == '1' && !mark[y + dy[i]][x + dx[i]]){
			dfs(y + dy[i], x + dx[i], tb);
		}
	}
	return;
}

int main(){
	while (1){
		string s; cin >> s;
		if (cin.eof()) return 0;

		memset(mark, false, sizeof(mark));

		vector<string> tb(12, "");
		tb[0] = s;
		REP(i, 1, 12){
			cin >> tb[i];
		}

		int ans = 0;
		FOR(i, 12){
			FOR(j, 12){
				if (tb[i][j] == '1' && !mark[i][j]){
					dfs(i, j, tb);
					ans++;
				}
			}
		}

		cout << ans << endl;
	}
	return 0;
}

