char digit[12][12];
int cnt = 0;

void dfs(int a, int b){
	digit[a][b] = '0';
	if(digit[a + 1][b] == '1' && a + 1 < 12) dfs(a + 1, b);
	if(digit[a - 1][b] == '1' && a - 1 >= 0) dfs(a - 1, b);
	if(digit[a][b + 1] == '1' && b + 1 < 12) dfs(a, b + 1);
	if(digit[a][b - 1] == '1' && b - 1 >= 0) dfs(a, b - 1);
}

int solve(){
	cnt = 0;
	for(int i = 0; i < 12; i++){
		for(int j = 0; j < 12; j++){
			if(digit[i][j] == '1'){
				dfs(i, j);
				cnt++;
			}
		}
	}
	return cnt;
}

int input(){
	char ch;
	for(int i = 0; i < 12; i++){
		for(int j = 0; j < 12; j++){
			cin >> ch;
			if(ch == EOF) return 0;
			digit[i][j] = ch;
		}
	}
	return 1;
}

int main(){
	while(input() != 0){
		cout << solve() << endl;
	}
	return 0;
}

