void wn(int i, int j){
	island[i][j] = '0';
	for(int k = -1; k <= 1; k+=2){
		if(i + k < 0 || j + k < 0)	continue;
		if(island[i+k][j] == '1'){
			wn(i+k, j);
		}
		if(island[i][j+k] == '1'){
			wn(i, j+k);
		}
	}

	return;	
}

