  if(riku[i][j].l==1 && riku[i][j-1].c!=2) check(riku,i  ,j-1);
  if(riku[i][j].u==1 && riku[i+1][j].c!=2) check(riku,i+1,j  );
  if(riku[i][j].t==1 && riku[i-1][j].c!=2) check(riku,i-1,j  );

