    for(j=3;j<n;j++)
      if(riku[i][j].c==1)
        {
	  //	  printf("i=%d, j=%d\n",i-2,j-2);
          shima++;
          check(riku,i,j);
        }
  return shima;
}

void con(Riku riku[18][18],int i,int j)
{
  if(riku[i][j].c==1)
    {
      riku[i+1][j].t=1;
      riku[i-1][j].u=1;
      riku[i][j+1].l=1;
      riku[i][j-1].r=1;
    }
}

