class UnionFind{
	vector<ll> rank;
	vector<ll> diff_weight;
	vector<ll> num;
public:
	vector<ll> par;

	UnionFind(ll N):par(N),num(N),rank(N),diff_weight(N){
		for(ll i=0;i<N;i++){
			par[i]=i;
			num[i]=1;
			rank[i]=0;
			diff_weight[i]=0;
		}
	}

	ll root(ll x){
		if(par[x]==x){
			return x;
		}
		ll r = root(par[x]);
		diff_weight[x]+=diff_weight[par[x]];
		return par[x]=r;
	}

	void unite(ll x,ll y){
		ll rx = root(x);
		ll ry = root(y);
		if (rx == ry) return;
		if(rank[rx]<rank[ry]) swap(rx,ry);
		if(rank[rx]==rank[ry]) ++rank[rx];
		par[ry] = rx;
		num[rx] += num[ry];
	}

	void relate(ll x,ll y,ll w){
		w+=weight(x),w-=weight(y);
		ll rx = root(x);
		ll ry = root(y);
		if (rx == ry) return;
		if(rank[rx]<rank[ry]){
			swap(rx,ry);
			w = -w;
		}
		if(rank[rx]==rank[ry]) ++rank[rx];
		par[ry] = rx;
		diff_weight[ry] = w;
		num[rx] += num[ry];
	}

	bool same(ll x,ll y){
		ll rx = root(x);
		ll ry = root(y);
		return rx == ry;
	}

	ll getsize(ll x){
		return num[root(x)];
	}

	ll weight(ll x){
		root(x);
		return diff_weight[x];
	}

	ll diff(ll x,ll y){
		return weight(y)-weight(x);
	}
};

int main(){
	cin.tie(0);
	ios::sync_with_stdio(false);

	vector<vector<int>> M(12,vector<int>(12));

	string S;
	while(cin>>S){
		REP(i,12){
			M[0][i]=S[i]-'0';
		}
		REP(i,11){
			cin>>S;
			REP(j,12){
				M[i+1][j]=S[j]-'0';
			}
		}
		UnionFind uf(144);
		REP(i,12){
			REP(j,12){
				if(M[i][j]){
					if(i>0&&M[i-1][j]){
						uf.unite(j+i*12,j+(i-1)*12);
					}
					if(j>0&&M[i][j-1]){
						uf.unite(j+i*12,j-1+i*12);
					}
				}
			}
		}
		int sum = 0;
		set<int> h;
		REP(i,144){
			h.insert(uf.root(i));
		}
		for(auto& x:h){
			int u=x%12,v=x/12;
			if(M[v][u]){
				sum++;
			}
		}

		out(sum);
	}
