                break;
            }
            REP(j, 12) island[i][j] = line[j] - '0';
        }

        int cnt = 0;
        REP(i, 12) REP(j, 12) {
            if (island[i][j]) {
                dfs(i, j);
                cnt++;
            }
        }
        cout << cnt << endl;
    }

    return 0;
}

