int cnt;
char field[13][13];

void solve(int x,int y)
{
	field[y][x] = '0';
	if(x+1 < 12)
		if(field[y][x+1] == '1')
			solve(x+1,y);
	if(x-1 >= 0)
		if(field[y][x-1] == '1')
			solve(x-1,y);
	if(y+1 < 12)
		if(field[y+1][x] == '1')
			solve(x,y+1);
	if(y-1 >= 0)
		if(field[y-1][x] == '1')
			solve(x,y-1);
}

