bool inside( int x, int y )
{
	return 0 <= x && x < 12 && 0 <= y && y < 12;
}

int bfs( vector<string> &board, unsigned int i, unsigned int j )
{
	if ( board[i][j] == '0' )
	{
		return 0;
	}

	board[i][j] = '0';

	for ( int d = 0; d < 4; d++ )
	{
		if ( inside( (int)i + direction[d][0], (int)j + direction[d][1] ) )
		{
			bfs( board, i + direction[d][0], j + direction[d][1] );
		}
	}

	return 1;
}

