{
	while ( true )
	{
		string buffer;
		vector<string> board;

		for ( int i = 0; i < 12; i++ )
		{
			if ( cin >> buffer )
			{
				board.push_back( buffer );
			}
			else
			{
				return 0;
			}
		}

		int res = 0;

		for ( unsigned int i = 0; i < 12; i++ )
		{
			for ( unsigned int j = 0; j < 12; j++ )
			{
				res += bfs( board, i, j );
			}
		}

		cout << res << endl;
	}

	return 0;
}

