  island[i][j] = '0';

  if( i - 1 >= 0 && island[i-1][j] == '1' ){
    dfs( i - 1 , j );
  }
  if( j - 1 >= 0 && island[i][j-1] == '1'){
    dfs( i , j - 1 );
  }
  if( i + 1 < 12 && island[i+1][j] == '1'){
    dfs( i + 1 , j );
  }
  if( j + 1 < 12 && island[i][j+1] == '1'){
    dfs( i , j + 1 );
  }
}

