    for(int i=0; i<12; i++) {
      for(int j=0; j<12; j++) d[i][j]=false;
    }
    int cnt=0;
    for(int i=0; i<12; i++) {
      for(int j=0; j<12; j++) {
	if(s[i][j]=='1' && !d[i][j]) {
	  cnt++;
	  queue<P> que;
