    for (int y = 1; y < 12; y++) {
      cin >> s;
      for (int x = 0; x < 12; x++)
        MAP[x][y] = (s[x] == '1')? true:false;
    }

    int ids = sink_all_islands();

    cout << ids << endl;
  }
}

