class union_find {
public:
	vector<int> dat;
	union_find(int n) {
		dat.assign(n, -1);
	}

	bool unite(int a, int b) {
		int da = find(a);
		int db = find(b);
		if (da != db){
			if (dat[da] > dat[db]) swap(da, db);
			dat[da] += dat[db];
			dat[db] = da;
		}
		return (da != db);
	}
	bool check(int a, int b) {
		return (find(a) == find(b));
	}
	int find(int a) {
		return ((dat[a] < 0) ? a : (dat[a] = find(dat[a])));
	}
	int size(int a) {
		return -dat[find(a)];
	}
};

