 vector <vector<int> > randb (vector <vector<int> >island,int si,int sj,int w,int h,int num){
   if( si+1 < h  && island[si+1][sj] == 1){
     island[si+1][sj] = num;
     island = randb(island,si+1,sj,w,h,num);
   }
   if( si-1 >= 0 && island[si-1][sj] == 1){
     island[si-1][sj] = num;
     island= randb(island,si-1,sj,w,h,num);
   }
   if( sj+1 < w  && island[si][sj+1] == 1){
     island[si][sj+1] = num;
     island = randb(island,si,sj+1,w,h,num);
   }
   if( sj-1 >= 0 && island[si][sj-1] == 1){
     island[si][sj-1] = num;
     island = randb(island,si,sj-1,w,h,num);
   }
   return island;
}

