{
	ild[i][j] = -1;

	if(i - 1 >= 0){
		if(ild[i - 1][j] == 1)
			erase_island(i - 1, j, ild);
	}
	if(i + 1 < 12){
		if(ild[i + 1][j] == 1)
			erase_island(i + 1, j, ild);
	}
	if(j - 1 >= 0){
		if(ild[i][j - 1] == 1)
			erase_island(i, j - 1, ild);
	}
	if(j + 1 < 12){
		if(ild[i][j + 1] == 1)
			erase_island(i, j + 1, ild);
	}
}

