int countIsland(char** world, int N){
    int count = 0;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            if (world[i][j] == '1') {
                destroyIsland(world, i, j, N);
                count++;
            }
        }
    }
    return count;
}

void destroyIsland(char** world, int i, int j, int N){
    world[i][j] = '0';
    if(i > 0){
        if (world[i - 1][j] == '1') {
            destroyIsland(world, i - 1, j, N);
        }
    }

    if(j > 0){
        if (world[i][j - 1] == '1') {
            destroyIsland(world, i, j - 1, N);
        }
    }

    if(i < N - 1){
        if (world[i + 1][j] == '1') {
            destroyIsland(world, i + 1, j, N);
        }
    }

    if(j < N - 1){
        if (world[i][j + 1] == '1') {
            destroyIsland(world, i, j + 1, N);
        }
    }

}

