#include<cstdio>
#include<algorithm>


//mapの最大値
#define INDEX_WHMAP_MAX		12
//マップ(１番大きいとする)
int wh_map[INDEX_WHMAP_MAX][INDEX_WHMAP_MAX];
//四方を見るための配列
const int h_tate[4] = {1 , -1 , 0 , 0};
const int h_yoko[4] = {0 , 0 , 1 , -1};
//入力方法がわからないから文字で受け取る
char str_nyuuryoku[INDEX_WHMAP_MAX][INDEX_WHMAP_MAX + 1];




//----- 1つめの島を見つける関数 -----//
int f_firstsima(void);

//----- 島を見つけた場合、陸続きのものがあるかどうか調べる関数 -----//
void f_search(int , int);

//----- マップを初期化する関数 -----//
int f_syokika(void);



//*****----- main -----*****

int main(void)
{
	while (1)
	{
		//最初に初期化する(EOFが返ってきたら終了)
		if (f_syokika() == 0)
		{
			break;
		}

		//戻り値を出力
		int ans = f_firstsima();
		printf("%d\n" , ans);
	}

	return 0;
}




//*****----- 1つめの島を見つける関数 -----*****//

int f_firstsima(void)
{
	//答えをカウントする変数
	int ans = 0;

	//マップの分だけforを回す
	for (int i = 0; i < 12; i++)
	{
		for (int j = 0; j < 12; j++)
		{
			//もしもまだ探索していない島だったら
			if (wh_map[i][j] == 1)
			{
				ans++;
				//再帰関数に現在座標を送って、陸続きのものを消してもらう
				f_search(i , j);
			}
		}
	}

	return ans;
}



//*****----- 島を見つけた場合陸続きなものがあるかどうか調べる関数 -----*****//

void f_search(int y , int x)
{
	//まず現在座標を0とする(島を消すため)
	wh_map[y][x] = 0;
	//四方を見るためにforを回す
	for (int i = 0; i < 4; i++)
	{
		//次の座標を宣言(マップがはみ出ないかどうかは重要である)
		int nextx = x + h_yoko[i];
		int nexty = y + h_tate[i];

		//はみでない
		if (nextx >= 0 && nextx < INDEX_WHMAP_MAX &&
			nexty >= 0 && nexty < INDEX_WHMAP_MAX)
		{
			//　+　陸続きである
			if (wh_map[nexty][nextx] == 1)
			{
				//再帰関数でさらに陸続きの場所がないか操作を続ける
				f_search(nexty , nextx);
			}
		}
	}
}




//*****----- マップ初期化関数の実体 -----*****//

int f_syokika(void)
{
	for (int i = 0; i < 12; i++)
	{
		//１行一気に入力されるらしい
		if (scanf("%s" , str_nyuuryoku[i]) == EOF)
		{
			return 0;
		}
	}

	//戻すのが面倒なので、文字列配列から整数配列へ移す
	for (int i = 0; i < 12; i++)
	{
		for (int j = 0; j < 12; j++)
		{
			wh_map[i][j] = str_nyuuryoku[i][j] - '0';
		}
	}

	return 1;
}

#include<iostream>
#include<stack>
using namespace std;

class P
{
	public:
	int x,y;
	P(int X,int Y)
	{
		x=X;
		y=Y;
	}
};

int main()
{
	int set[2]={1,-1};
	char field[52][52]={""};

	while((cin>>field[1])!=NULL)
	{
		int flag[52][52]={0};
		stack<P> st;
		int ans=0;

		for(int i=2;i<=12;i++)
		{
			cin>>field[i];		
		}

		for(int i=1;i<=12;i++)
		{
			for(int j=1;j<=12;j++)
			{
				if(field[i][j]!='1' || flag[i][j]==1)continue;
				st.push(P(i,j));
				flag[i][j]=1;
				ans++;

				while(!st.empty())
				{
					int x2=st.top().x;
					int y2=st.top().y;
					st.pop();

					for(int k=0;k<2;k++)
					{
						if(field[x2+set[k]][y2]!='1' ||flag[x2+set[k]][y2]==1)continue;
						flag[x2+set[k]][y2]=1;
						st.push(P(x2+set[k],y2));
					}
					for(int k=0;k<2;k++)
					{
						if(field[x2][y2+set[k]]!='1' ||flag[x2][y2+set[k]])continue;
						flag[x2][y2+set[k]]=1;
						st.push(P(x2,y2+set[k]));
					}
				}
			}
		}
		cout<<ans<<endl;
	}
	return 0;
}

