#include<iostream>
#include<queue>
#include<cstring>
using namespace std;

queue<int>Q;
int z[100][100], x[100][100], sum;

void BFS(int col,int x1,int y1) {
	Q.push(x1 * 100 + y1);
	int a, b;
	while (!Q.empty()) {
		a = Q.front() / 100;
		b = Q.front() % 100;
		if (z[a + 1][b] == 0 && x[a + 1][b] == 0) {
			Q.push(Q.front() + 100);
			x[a + 1][b] = col;
		}
		if (z[a - 1][b] == 0 && x[a - 1][b] == 0) {
			Q.push(Q.front() - 100);
			x[a - 1][b] = col;
		}
		if (z[a][b + 1] == 0 && x[a][b + 1] == 0) {
			Q.push(Q.front() + 1);
			x[a][b + 1] = col;
		}
		if (z[a][b - 1] == 0 && x[a][b - 1] == 0) {
			Q.push(Q.front() + 1);
			x[a][b - 1] = col;
		}
		Q.pop();
	}
}

int main() {
	while (cin >> z[1][1]) {
		sum = 0;
		for (int i = 1; i <= 12; i++) {
			for (int j = 1; j <= 12; j++) {
				if (i + j != 2) { cin >> z[i][j]; }
			}
		}
		for (int i = 1; i <= 12; i++) {
			for (int j = 1; j <= 12; j++) {
				if (x[i][j] == 0) {
					sum++;
					BFS(sum, i, j);
				}
			}
		}
		cout << sum << endl;
		memset(x, 0, sizeof(x));
	}
}

#include <iostream>

#define N 12

std::string island[N];

void dfs( int i, int j ) {
    int dx[] = { -1,  0,  1,  0 };
    int dy[] = {  0, -1,  0,  1 };
    island[i][j] = '0';
    for (int k=0; k<4; ++k) {
        int x = i+dx[k], y = j+dy[k];
        if ( 0<=x && x<N && 0<=y && y<N && island[x][y] == '1' ) {
            dfs( x, y );
        }
    }
}

void solve() {
    unsigned int num = 0;
    for (int i=0; i<N; ++i) {
        for (int j=0; j<N; ++j) {
            if ( island[i][j] == '1' ) {
                ++num;
                dfs( i, j );
            }
        }
    }
    std::cout << num << std::endl;
}

int main() {
    while ( std::cin >> island[0] ) {
        for (int i=1; i<N; ++i) {
            std::cin >> island[i];
        }
        solve();
    }
    return 0;
}

