int dx[] = {1, -1, 0, 0}, dy[] = {0, 0, 1, -1};

void mark(int x, int y) {
    if (field[y][x] == '1') field[y][x] = '0';
    for (int i = 0; i < 4; i++) {
        int nx = x + dx[i], ny = y + dy[i];
        if (nx < 0 || ny < 0 || nx >= W || ny >= H) continue;
        if (field[ny][nx] == '0') continue;
        mark(nx, ny);
    }
}

