bool island[H][W];
bool arrived[H][W];

bool is_not_arrived(int x, int y) {
	if(x<0 || W<=x || y<0 || H<=y) return false;

	if(island[y][x] && !arrived[y][x]) {
		arrived[y][x] = true;
		is_not_arrived(x+1,y);
		is_not_arrived(x-1,y);
		is_not_arrived(x,y+1);
		is_not_arrived(x,y-1);
		return true;
	}

	return false;
}

