		if(cin.eof()) break;

		int ans = 0;
		for(int i = 0; i < H; i++) {
			for(int j = 0; j < W; j++) {
				if(is_not_arrived(j,i)) {
					ans++;
				}
			}
		}

		cout << ans << endl;
	}
}

