int *map = new int[12 * 12 * 20];
static int count;

void loop(int i){
	map[i] = 0;
	if ((i % 144) / 12 > 0) {
		if (map[i - 12] == 1) {
			l.push_front(i - 12);
		}
	}
	if ((i % 144) / 12 < 11) {
		if (map[i + 12] == 1) {
			l.push_front(i + 12);
		}
	}
	if ((i % 144) % 12 > 0) {
		if (map[i - 1] == 1) {
			l.push_front(i - 1);
		}
	}
	if ((i % 144) % 12 < 11) {
		if (map[i + 1] == 1) {
			l.push_front(i + 1);
		}
	}
	l.unique();
	if (l.empty()) {
		return;
	}
	int front = l.front();
	l.pop_front();
	loop(front);
}

int main(int argc, char **argv)
{
	int count = 0;
	char c;
	for (int i = 0; i < 12 * 12 * 20 && cin >> c; i++) {
		map[i] = c - '0';
		cout << c;
		if (i % 12 == 11) {
			cout << endl;
		}
	}
	for (int i = 0; i < 12 * 12 * 20; i++) {
		if (map[i] == 1) {
			l.push_front(i);
			loop(i);
			count++;
		}
		if (i % 144 == 143) {
			cout << count << endl;
			count = 0;
		}
	}
	return 0;
}

