using namespace std;

void islandExplore( char m[14][14], int i, int j )
{
    m[i][j] = '2';                       //島探検済み

    if( m[i-1][j] != '1' && m[i+1][j] != '1' && m[i][j-1] != '1' && m[i][j+1] != '1' )      //未探索の島が無い場合終了
        return;

    if( m[i-1][j] == '1' )              //左の領域を探索
        islandExplore( m, i - 1, j );
    if( m[i+1][j] == '1' )              //右の領域を探索
        islandExplore( m, i + 1, j );
    if( m[i][j+1] == '1' )              //下の領域を探索
        islandExplore( m, i, j + 1 );
    if( m[i][j-1] == '1' )              //上の領域を探索
        islandExplore( m, i, j - 1 );
}

int main()
{   
    char map[14][14];
    for( int i = 0; i < 14; i++ )
        for( int j = 0; j < 14; j++ )
            map[i][j] = '0';

    string str;
    int s = 1;
    while( cin >> str )
    {
        int cnt = 0;                            //初期化
        for( int i = 0; i < str.size(); i++ )   //1行ずつ入力
            map[i+1][s] = str[i];

        if( s == 12 )
        {
            for( int i = 1; i <= 12; i++ )          //マスを一つずつ探索
                for( int j = 1; j <= 12; j++ )
                {
                    if( map[i][j] == '1' )
                    {
                        cnt++;
                        islandExplore( map, i, j ); //島をすみずみ探検して、繋がりを把握
                    }
                }
            cout << cnt << endl;
            s = 0;
        }
        s++;
    }
    return 0;
}

