int dy[4] = {0,-1,0,1};

void func(char (*imap)[12], int x, int y){
  imap[x][y] = '0';
  for(int k=0; k<4; ++k){
      int nx = x + dx[k], ny = y + dy[k];
      if(nx < 0 || 12 <= nx) continue;
      if(ny < 0 || 12 <= ny) continue;
      if(imap[nx][ny] == '0') continue;
      else func(imap,nx,ny);
  }
}

int main(void){
  char island_map[12][12];
