int map[12][12];
char nyuryoku[12][12];
int count=0;
void fun(int x,int y,char masume){
	if(map[x][y]!=1)
		return;
	if(masume=='0'){
		map[x][y]=2;
		count--;
		return;
	}
	if(masume!=nyuryoku[x][y]){
		return;
	}
	if((x<0)||(12<=x)||(y<0)||(12<=y)){
		return;
	}
	map[x][y]=0;
	fun(x+1,y,nyuryoku[x][y]);
	fun(x,y+1,nyuryoku[x][y]);
	fun(x-1,y,nyuryoku[x][y]);
	fun(x,y-1,nyuryoku[x][y]);
}

int main(){
	string a;
	while(cin >> a){
		for(int i=0;i<12;i++)
			map[0][i]=a[i];
	for(int i=1;i<12;i++){
		cin >> a;
		for(int j=0;j<12;j++){
			map[i][j]=1;
			nyuryoku[i][j]=a[j];
		}
	}

	for(int i=0;i<12;i++){
		for(int j=0;j<12;j++){
			if(map[i][j]==1){
				fun(i,j,nyuryoku[i][j]);
				count++;
			}
		}
	}
	cout << count << endl;
	}
}

#include <iostream>
