int fld[12][12];
int p[12][12];
const pair<int, int> d[]={{1, 0}, {-1, 0}, {0, 1}, {0, -1}};

bool is_fld(pair<int, int> nex){
    return (0<=nex.first&&nex.first<12&&0<=nex.second&&nex.second<12);
}

void dfs(int i, int j){
    p[i][j]=1;
    for(int k=0; k<4; k++){
        pair<int, int> nex=make_pair(i+d[k].first, j+d[k].second);
        if(is_fld(nex)){
            if(fld[nex.first][nex.second]==1&&p[nex.first][nex.second]==0){
                dfs(nex.first, nex.second);
            }
        }
    }

}

int main(){

    char a;
    while(scanf("%c", &a)!=EOF){
        for(int i=0; i<12; i++){
            for(int j=0; j<12; j++){
                char c;
                cin>> c;
                fld[i][j]=atoi(&c);
            }
