        int ans=0;
        for(int i=0; i<12; i++){
            for(int j=0; j<12; j++){
                if(fld[i][j]==1&&p[i][j]==0){
                    dfs(i, j);
                    ans++;
                }
            }
        }

        cout<< ans<< endl;

        fill(p[0], p[12], 0);
        fill(fld[0], fld[12], 0);
    }


    return 0;
}

