int ans;
char land[TMP][TMP];

void dfs(int x, int y) {
	if(land[x][y] == '1'){
		land[x][y] = '0';
		if(land[x + 1][y] == '1' && x + 1 < TMP && x >= 0) dfs(x + 1, y);
		if(land[x - 1][y] == '1' && x - 1 >= 0 && x < TMP) dfs(x - 1, y);
		if(land[x][y + 1] == '1' && y + 1 < TMP && y >= 0) dfs(x, y + 1);
		if(land[x][y - 1] == '1' && y - 1 >= 0 && y < TMP) dfs(x, y - 1);
	}
	return ;
}

