			rep(j, TMP) {
				if(i == 0 && j == 0) continue;
				cin >> land[i][j];
			}
		}
		rep(i, TMP){
			rep(j, TMP) {
				if(land[i][j] == '1') {
					dfs(i, j);
					++ans;
				}
			}
		}
		cout << ans << endl;
	}
	return 0;
}

