		draw(x, y-1);
	}
	//下側検索
	if(field[x][y+1] == 1 && y != 11) {
		draw(x, y+1);
	}
	//右側検索
	if(field[x+1][y] == 1 && x != 11) {
		draw(x+1, y);
	}
	//左側検索
	if(field[x-1][y] == 1 && x != 0) {
		draw(x-1, y);
	}
}

