int *map = new int[12 * 12];
static int count;

void loop(int i, int j){
	map[i * 12 + j] = 0;
	if (i > 0) {
		if (map[(i - 1) * 12 + j] == 1) {
			l.push_front((i - 1) * 12 + j);
		}
	}
	if (i < 11) {
		if (map[(i + 1) * 12 + j] == 1) {
			l.push_front((i + 1) * 12 + j);
		}
	}
	if (j > 0) {
		if (map[i * 12 + j - 1] == 1) {
			l.push_front(i * 12 + j - 1);
		}
	}
	if (j < 11) {
		if (map[i * 12 + j + 1] == 1) {
			l.push_front(i * 12 + j + 1);
		}
	}
	l.unique();
	if (l.empty()) {
		return;
	}
	int front = l.front();
	l.pop_front();
	loop(front / 12, front % 12);
}

